import React from 'react';

class About extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            counter:0
        };
        console.log("Constructor please")
    }
    updatecounter(){
        this.setState((prevState,props)=>{
            return {counter: prevState.counter+1}
        })
    }

    render(){
        return(
        <>
        <h2>Find out about the natures tours here..</h2>
        <button style={{marginBottom:"10px", marginTop: "5px"}} className="btn btn-primary" onClick={()=>this.updatecounter()}> {" "} Hit Counter{this.state.counter}</button>
        </>
        );
    }

    componentDidMount(){
        console.log("Component mounted on DOM");
    }

    componentDidUpdate(){
        console.log("Component updating");
    }

    componentWillUnmount(){
        console.log("Component unmounting");
    }
}

About.defaultProps={
    name: "GoTours",
    tag: "Enjoy travelling"
}

export default About;
